import React, { useState } from 'react'
const hello = 0
function Reducer() {
    const [hello,setHello]=useState(0)
  return (
    <div>
        <button onClick={()=>setHello(hello +1)} >+</button>
        <p>{hello}</p>
        <button onClick={()=>setHello(hello -1)} >-</button>

    </div>
  )
}

export default Reducer
