import React, { useEffect, useState } from 'react'
function Reducer() {
    const [hello,setHello]=useState(0)
    const happy =()=>{


      return(
        <p>{hello}</p>
      )
    }
    useEffect(()=>{
      setInterval(happy,1000)
    }
    ,[hello])
  return (
    <div>
        <button onClick={()=>setHello(hello +1)} >+</button>
        <span>{hello}</span>
        <button onClick={()=>setHello(hello -1)} >-</button>
    </div>
  )
}

export default Reducer
