import React, { useEffect, useState } from 'react'
function Reducer() {
    const [hello,setHello]=useState(0)
    const happy =()=>{
      return(
        <p>{setHello(hello+1)}</p>
      )
    }
    useEffect(()=>{
      setInterval(happy,1000)
    }
    ,[hello])
  return (
    <div>
      <h1>{hello}</h1>
    </div>
  )
}

export default Reducer
